{% if user ?? false %}
  <p>There is a user here and their username is <em>{{ user.username ?? 'NOT SET' }}</em>.</p>
{% elseif element ?? false %}
  <p>There is an element here and its title is <em>{{ element.title ?? 'NOT SET' }}</em>.</p>
{% else %}
  <p>There is no element here.</p>
{% endif %}

{% set pageParam = 'guide-REPLACE_GUIDE_SLUG' %}
{% set currentPage = craft.app.request.queryParams[pageParam] ?? 1 %}

<div class="buttons">
    {% if currentPage > 1 %}
        {% set previousPageParams = craft.app.request.queryParams|merge({ (pageParam): currentPage|integer - 1 }) %}

        {{ craft.guide.component('button', { label: 'Previous Page', url: cpUrl(craft.app.request.getSegments|join('/'), previousPageParams) }) }}
    {% endif %}

    {% set nextPageParams = craft.app.request.queryParams|merge({ (pageParam): currentPage|integer + 1 }) %}

    {{ craft.guide.component('button', { label: 'Next Page', url: cpUrl(craft.app.request.getSegments|join('/'), nextPageParams) }) }}
</div>

